mapper.update:
UPDATE BOARD
SET count= #{count} + 1
WHERE bseq=#{bseq}

mapper.insert:
INSERT INTO BOARD (
					BSEQ
				  , TITLE
				  , ID
				  , CONTENT
				  , REGDATE
				  , COUNT
				  )
		   VALUES (BOARD_SEQ.NEXTVAL
		         , #{title}
		         , #{id}
		         , #{content}
		         , SYSDATE
		         , 0
		         )

INSERT INTO BOARD_IMAGE (
					BOARD_FSEQ
				  , BSEQ
				  , BOARD_IMAGE
				  )
		   VALUES (BOARD_FSEQ_SEQ.NEXTVAL
		         , #{bSeq}
		         , #{fileName}
		         )
